'use strict';

const getPolyfill = require('./polyfill');
const define = require('define-properties');

module.exports = function shimEntries() {
    const polyfill = getPolyfill();
    define(
        Object,
        { entries: polyfill },
        {
            entries: function testEntries() {
                return Object.entries !== polyfill;
            },
        }
    );
    return polyfill;
};
