'use strict';

const getPolyfill = require('./polyfill');
const define = require('define-properties');

module.exports = function shimValues() {
    const polyfill = getPolyfill();
    define(
        Object,
        { values: polyfill },
        {
            values: function testValues() {
                return Object.values !== polyfill;
            },
        }
    );
    return polyfill;
};
